mapper.select:
select

from sm_user

	and id = #{id}
	and user_name = #{userName}
	and user_img = #{userImg}
	and mobile = #{mobile}
	and password = #{password}
	and wechat_openid = #{wechatOpenid}
	and email = #{email,jdbcType=VARCHAR},
	and last_login_date = #{lastLoginDate}
	and is_enable = 0
	and create_date = #{createDate}
	and update_date = #{updateDate}

mapper.delete:
update sm_user
set is_enable=1,update_date = now()
where id = #{id,jdbcType=INTEGER}

mapper.update:
update sm_user
user_name = #{userName,jdbcType=VARCHAR}, user_img = #{userImg,jdbcType=VARCHAR}, mobile = #{mobile,jdbcType=INTEGER}, password = #{password,jdbcType=VARCHAR}, wechat_openid = #{wechatOpenid,jdbcType=VARCHAR}, email = #{email,jdbcType=VARCHAR}, last_login_date = #{lastLoginDate,jdbcType=TIMESTAMP}, is_enable = #{isEnable,jdbcType=CHAR}, create_date = #{createDate,jdbcType=TIMESTAMP}, update_date = now(),
where id = #{id,jdbcType=INTEGER}
