mapper.select:
select

from sm_user

    id = #{id}
	user_name = #{userName}
	user_img = #{userImg}
	mobile = #{mobile}
	password = #{password}
	wechat_openid = #{wechatOpenid}
	email = #{email,jdbcType=VARCHAR},
	last_login_date = #{lastLoginDate}
	is_enable = #{isEnable}
	create_date = #{createDate}
	update_date = #{updateDate}

mapper.update:
update sm_user
user_name = #{userName,jdbcType=VARCHAR}, user_img = #{userImg,jdbcType=VARCHAR}, mobile = #{mobile,jdbcType=VARCHAR}, password = #{password,jdbcType=VARCHAR}, wechat_openid = #{wechatOpenid,jdbcType=VARCHAR}, email = #{email,jdbcType=VARCHAR}, last_login_date = #{lastLoginDate,jdbcType=TIMESTAMP}, is_enable = #{isEnable,jdbcType=CHAR}, create_date = #{createDate,jdbcType=TIMESTAMP}, update_date = #{updateDate,jdbcType=TIMESTAMP},
where id = #{id,jdbcType=INTEGER}
